(605, 161, '_form', '<fieldset>\n\n                        <div class=\"form-field\">\n                           [text cname class:full-width placeholder \"your name\"]\n\n                        </div>\n\n                        <div class=\"form-field\">\n                            [email cemail class:full-width placeholder \"your email\"]\n                        </div>\n\n                        <div class=\"form-field\">\n                           [text cwebsite class:full-width placeholder \"website\"]\n                        </div>\n\n                        <div class=\"message form-field\">\n                        [textarea cmessage class:full-width placeholder \"your message\"]\n                        </div>\n\n                        [submit class:submit class:btn class:btn--primary class:full-width \"Send\"]\n\n                    </fieldset>'),
(606, 161, '_mail', 'a:9:{s:6:\"active\";b:1;s:7:\"subject\";s:32:\"Hello Beautiful \"[your-subject]\"\";s:6:\"sender\";s:37:\"Hello Beautiful <arifcse11@gmail.com>\";s:9:\"recipient\";s:19:\"arifcse11@gmail.com\";s:4:\"body\";s:175:\"From: [your-name] <[your-email]>\nSubject: [your-subject]\n\nMessage Body:\n[your-message]\n\n-- \nThis e-mail was sent from a contact form on Hello Beautiful (http://localhost/lwhh)\";s:18:\"additional_headers\";s:22:\"Reply-To: [your-email]\";s:11:\"attachments\";s:0:\"\";s:8:\"use_html\";b:0;s:13:\"exclude_blank\";b:0;}'),
